import type { FormSchemaGetter, VbenFormProps } from '#/adapter/form';
import type { VxeGridProps } from '#/adapter/vxe-table';

import { $t } from '@vben/locales';
import { getPopupContainer } from '@vben/utils';

import dayjs from 'dayjs';

import { getTagList } from '#/api/fms/fileTag';
import { getDictOptions } from '#/utils/dict';
import { renderDict } from '#/utils/render';

export const querySchema: FormSchemaGetter = () => [
  {
    component: 'Input',
    fieldName: 'fileName',
    label: '文件名',
  },
  {
    component: 'Select',
    componentProps: {
      getPopupContainer,
      options: getDictOptions('system_file_type'),
    },
    fieldName: 'fileType',
    label: '文件类型',
  },
  {
    component: 'RangePicker',
    fieldName: 'period',
    label: '创建时间',
  },
];
export const columns: VxeGridProps['columns'] = [
  { title: 'ID', field: 'id', visible: false },
  {
    title: '文件路径',
    field: 'path',
    showOverflow: true,
  },
  {
    title: '文件原名',
    field: 'name',
    showOverflow: true,
  },
  {
    title: '文件类型',
    field: 'fileType',
    slots: {
      default: ({ row }) => {
        return renderDict(row.fileType, 'system_file_type');
      },
    },
  },
  {
    title: $t('fms.file.fileSize'),
    field: 'size',
    slots: {
      default: (e) => {
        if (e.row.size > 1_073_741_824) {
          return `${(e.row.size / 1_073_741_824).toFixed(2)}GB`;
        } else if (e.row.size > 1_048_576) {
          return `${(e.row.size / 1_048_576).toFixed(2)}MB`;
        } else {
          return `${(e.row.size / 1024).toFixed(2)}KB`;
        }
      },
    },
  },
  {
    title: $t('component.cropper.preview'),
    field: 'publicPath',
    cellRender: { name: 'CellImage' },
  },
  {
    title: '创建时间',
    field: 'createdAt',
    formatter: ({ cellValue }) => {
      return cellValue ? dayjs(cellValue).format('YYYY-MM-DD') : '';
    },
  },
  {
    field: 'action',
    fixed: 'right',
    slots: { default: 'action' },
    title: '操作',
    width: 200,
  },
];

export const dataFormSchemas: VbenFormProps = {
  schema: [
    {
      fieldName: 'id',
      label: 'ID',
      component: 'Input',
      dependencies: {
        show: false,
        triggerFields: ['id'],
      },
    },
    {
      fieldName: 'name',
      label: $t('fms.file.fileName'),
      component: 'Input',
    },
    {
      fieldName: 'tagIds',
      label: $t('fms.tag.tag'),
      component: 'ApiSelect',
      componentProps: {
        api: getTagList,
        params: {
          page: 1,
          pageSize: 1000,
          name: '',
        },
        resultField: 'data.data',
        labelField: 'name',
        valueField: 'id',
        multiple: true,
      },
    },
  ],
};

/**
 * 图片加载失败的fallback
 */
export const fallbackImageBase64 =
  'data:image/png;base64,';
